"use strict";(self.webpackChunkwaste_of_space_wiki=self.webpackChunkwaste_of_space_wiki||[]).push([[3609],{70253:e=>{e.exports=JSON.parse('{"tag":{"label":"Weapons","permalink":"/Pilot.lua-Luau-LSP/tags/weapons","allTagsPath":"/Pilot.lua-Luau-LSP/tags","count":18,"items":[{"id":"objects/Artillery","title":"Artillery","description":"Fires a fast-moving metal shell upon being triggered.","permalink":"/Pilot.lua-Luau-LSP/objects/Artillery"},{"id":"objects/AutomaticLaser","title":"AutomaticLaser","description":"Fires a continous set of lasers when powered. Like most other energy/thermal weapons, it generates heat when powered. It generates up to 65 degrees of heat, meaning you will need atleast 2 or 3 coolers.","permalink":"/Pilot.lua-Luau-LSP/objects/AutomaticLaser"},{"id":"objects/BurstLaser","title":"BurstLaser","description":"Fires 5 rapid sets of concentrated laser beams. Each beam does a mediate amount of damage.","permalink":"/Pilot.lua-Luau-LSP/objects/BurstLaser"},{"id":"objects/Cannon","title":"Cannon","description":"Fires a cannon ball when triggered or clicked.","permalink":"/Pilot.lua-Luau-LSP/objects/Cannon"},{"id":"objects/EnergyBomb","title":"EnergyBomb","description":"Causes a powerful explosion when triggered. Often used in space combat as a means of a powerful missile or torpedo.","permalink":"/Pilot.lua-Luau-LSP/objects/EnergyBomb"},{"id":"objects/EnergyGun","title":"EnergyGun","description":"Fires a powerful shot of energy when provided power and triggered.","permalink":"/Pilot.lua-Luau-LSP/objects/EnergyGun"},{"id":"objects/Explosive","title":"Explosive","description":"Causes an explosion when triggered. You can use a delay wire to make it explode after a few seconds and not immediately.","permalink":"/Pilot.lua-Luau-LSP/objects/Explosive"},{"id":"objects/Flamethrower","title":"Flamethrower","description":"Shoots a stream of flaming gasoline to ignite your foes.","permalink":"/Pilot.lua-Luau-LSP/objects/Flamethrower"},{"id":"objects/Gun","title":"Gun","description":"Fires a harmful bullet when triggered.","permalink":"/Pilot.lua-Luau-LSP/objects/Gun"},{"id":"objects/ImpulseCannon","title":"ImpulseCannon","description":"Blasts a harmless burst of energy which sends anything nearby soaring at a high velocity.","permalink":"/Pilot.lua-Luau-LSP/objects/ImpulseCannon"},{"id":"objects/Laser","title":"Laser","description":"Fires a concentraded beam of energy when powered, which can be used to damage and set objects on fire.","permalink":"/Pilot.lua-Luau-LSP/objects/Laser"},{"id":"objects/PlasmaCannon","title":"PlasmaCannon","description":"Fires an orb of unstable plasma out of the barrel exploding on impact.","permalink":"/Pilot.lua-Luau-LSP/objects/PlasmaCannon"},{"id":"objects/Railgun","title":"Railgun","description":"Fires a piercing Rail at high speeds. A long range weapon. Requires Rails and electricity for ammo. The damage increases for every 10 studs of length.","permalink":"/Pilot.lua-Luau-LSP/objects/Railgun"},{"id":"objects/RepairLaser","title":"RepairLaser","description":"Fires a beam that repairs objects. Repairs 1 HP/s.","permalink":"/Pilot.lua-Luau-LSP/objects/RepairLaser"},{"id":"objects/Shotgun","title":"Shotgun","description":"Fires a shot of harmful pellets when triggered.","permalink":"/Pilot.lua-Luau-LSP/objects/Shotgun"},{"id":"objects/TractorBeam","title":"TractorBeam","description":"Fires a beam that exerts force on the object it hits. More effective in space due to low gravity, but still decently strong when affected by gravity. Force is affected by size and configuration.","permalink":"/Pilot.lua-Luau-LSP/objects/TractorBeam"},{"id":"objects/Warhead","title":"Warhead","description":"Causes a powerful explosion when triggered. You can use a delay wire to make it explode after a few seconds and not immediately.","permalink":"/Pilot.lua-Luau-LSP/objects/Warhead"},{"id":"objects/ZapWire","title":"ZapWire","description":"Electrocutes players when touched. Can be used to set flammable objects on fire.","permalink":"/Pilot.lua-Luau-LSP/objects/ZapWire"}],"unlisted":false}}')}}]);